(()=>{document.querySelector("#weather_display");const e=document.querySelector("#city"),t=document.querySelector("#user_submit"),o=document.querySelector("#location"),n=document.querySelector("#curr_temp"),r=document.querySelector("#desc_weather"),c=document.querySelector("#wind_speed");t.addEventListener("click",(async function(){try{const t=await fetch("https://api.openweathermap.org/data/2.5/weather?q="+e.value+"&appid=48f9e697bfab037187eccf9b6153b9e9&units=imperial",{mode:"cors"}),i=await t.json();console.log(i),console.log(i.main.temp),console.log(i.name),console.log(i.sys.country),console.log(i.weather[0].description),console.log(i.wind);const s=JSON.stringify(i.wind),a=JSON.stringify(i.sys.country),l=JSON.stringify(i.name),u=JSON.stringify(i.weather[0].description),d=JSON.stringify(i.main.temp);o.innerHTML=l+","+a,n.innerHTML="The current temperature is "+d+" F",r.innerHTML="Today's Forecast: "+u,c.innerHTML="Wind Speed: "+s}catch(e){console.log("error")}}))})();